func extractParameters(myList []int, index int) (int, int, int, int) {
	instruction := myList[index]

	// Didn't use Math.Mod to avoid converting in float64

	param3Mode := instruction / 10000
	param2Mode := instruction/1000 - (param3Mode * 10)
	param1Mode := instruction/100 - (param2Mode * 10) - (param3Mode * 100)
	opcode := instruction - (param1Mode * 100) - (param2Mode * 1000) - (param3Mode * 10000)
	param1 := 0
	param2 := 0
	param3 := 0

	if index+1 < len(myList) {
		if param1Mode == 0 {
			if myList[index+1] < len(myList) {
				param1 = myList[myList[index+1]]
			} else {
				param1 = 0
			}
		} else {
			param1 = myList[index+1]
		}
		if index+2 < len(myList) {
			if param2Mode == 0 {
				if myList[index+2] < len(myList) {
					param2 = myList[myList[index+2]]
				} else {
					param2 = 0
				}
			} else {
				param2 = myList[index+2]
			}
			if index+3 < len(myList) {

				param3 = myList[index+3]
			}
		}
	}

	return opcode, param1, param2, param3

}

func runProgram(myList []int, val1 int) {
	continuer := true
	index := 0
	total := 0
	for continuer {

		opcode, param1, param2, param3 := extractParameters(myList, index)

		switch opcode {
		case 99:
			continuer = false
			break
		case 1:
			total = param1 + param2
			myList[myList[index+3]] = total
			index += 4
			break
		case 2:
			total = param1 * param2
			myList[myList[index+3]] = total
			index += 4
			break
		case 3:
			myList[myList[index+1]] = val1
			index += 2
			break
		case 4:
			fmt.Print("Diagnostic code : ")
			fmt.Println(param1)
			index += 2
			break
		case 5:
			if param1 != 0 {
				index = param2
			} else {
				index += 3
			}
			break
		case 6:
			if param1 == 0 {
				index = param2
			} else {
				index += 3
			}
			break
		case 7:
			if param1 < param2 {
				myList[param3] = 1
			} else {
				myList[param3] = 0
			}
			index += 4
			break
		case 8:
			if param1 == param2 {
				myList[param3] = 1
			} else {
				myList[param3] = 0
			}
			index += 4
			break
		}
	}

}

